import { Component, OnInit } from '@angular/core';
import {OrderService} from '../order.service';

@Component({
  selector: 'app-pedido',
  templateUrl: './order.component.html',
  styleUrls: ['./order.component.sass']
})
export class OrderComponent implements OnInit {

  constructor(private orderService: OrderService) {
  }

  qtyAvailableDrinks: number[] = [0];
  item1 = 0;
  item2 = 0;
  order: string;
  comments = '';
  total: number;

  cart = {};

  products: Products = {
    categories: [
      {
        id: '1',
        label: 'Menu',
        products: [
          {
            id: '1',
            label: 'Dog no Molho',
            img: 'data:image/jpeg;base64,',
            price: 9.99,
            qtyAvailable: 10,
            description: [
              'Molho 100% caseiro, queijo, batata palha, maionese e pão ninho.',
              'Acompanha refri: Coca ou Guaraná.\n' +
              '\n' +
              '+\n' +
              '\n' +
              'Maionese Tradicional, Ketchup e Maionese de Alho']
          },
          {
            id: '2',
            label: 'Dog no Molho Especial',
            img: 'data:image/jpeg;base64,',
            price: 11.99,
            qtyAvailable: 10,
            description: [
              'Molho 100% caseiro, salsicha recheada com queijo, pão ninho, mais queijo no pão, ' +
              'maionese caseira e bastante bacon (batata palha vai separada).',
              'Acompanha refri: Coca ou Guaraná.\n'
            ]
          },
          {
            id: '3',
            label: 'Extras',
            subProducts: [
              {
                id: '4',
                label: 'Bombom de Uva',
                price: 4,
                qtyAvailable: 10
              },
              {
                id: '5',
                label: 'Bacon',
                price: 3,
                qtyAvailable: 10
              },
              {
                id: '6',
                label: 'Queijo',
                price: 2,
                qtyAvailable: 10
              },
              {
                id: '7',
                label: 'Salsicha',
                price: 2,
                qtyAvailable: 10
              }
            ]
          }
        ]
      }
    ]
  };


  extras: Items = {
    items: [{
      label: 'Bombom de Uva',
      price: 4,
      qty: 0
    },
    {
      label: 'Bacon',
      price: 3,
      qty: 0
    },
    {
      label: 'Queijo',
      price: 2,
      qty: 0
    },
    {
      label: 'Salsicha',
      price: 2,
      qty: 0
    }]
  };

  drinks = {
    items: [
    {
      label: 'Coca-Cola',
      qtd: 0
    },
    {
      label: 'Guaraná',
      qtd: 0
    },
    {
      label: 'Suco de Abacaxi',
      qtd: 0
    },
    {
      label: 'Suco de Goiaba',
      qtd: 0
    },
    {
      label: 'Suco de Laranja',
      qtd: 0
    },
    {
      label: 'Suco de Maracujá',
      qtd: 0
    },
    {
      label: 'Suco de Uva',
      qtd: 0
    }],
  };

  totalDrinks = 0;
  totalDrinksAvailable = 0;

  valid = false;

  ngOnInit() {
    this.updateTotal();
    this.validate();
  }

  updateTotal() {
    this.total = 2;
    this.total += this.item1 ? (this.item1 * 9.99) : 0;
    this.total += this.item2 ? (this.item2 * 11.99) : 0;
    this.total += this.orderService.calculateExtras(this.extras);

    this.atualizarRefrisDisponiveis();
    this.validate();
  }

  atualizarRefrisDisponiveis() {
    this.totalDrinks = 0;
    for (const drink of this.drinks.items) {
      this.totalDrinks += drink.qtd;
    }

    this.totalDrinksAvailable = this.item1 + this.item2;
    this.qtyAvailableDrinks = [];
    for (let i = 0 ; i <= this.totalDrinksAvailable ; i++) {
      this.qtyAvailableDrinks.push(i);
    }
    this.validate();
  }

  updateOrder() {
    this.order = this.orderService.buildOrder(this.item1, this.item2, this.comments, this.extras, this.drinks);
  }

  validate() {
    if (this.item1 === 0 && this.item2 === 0) {
      this.valid = false;
      return;
    }

    if (this.totalDrinks > this.totalDrinksAvailable) {
      this.valid = false;
      return;
    }
    this.valid = true;
  }

  createArrayIndex(qtyAvailable: number) {
    return Array.from(Array((qtyAvailable + 1)).keys());
  }
}
